import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { useNavigate } from 'react-router-dom';
import './Inbox.css';

const Inbox = () => {
  const [messages, setMessages] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const navigate = useNavigate();

  useEffect(() => {
    fetchMessages();
  }, []);

  const fetchMessages = async () => {
    try {
      const token = localStorage.getItem('token');
      const response = await axios.get('http://localhost:5001/api/messages', {
        headers: { Authorization: `Bearer ${token}` }
      });
      setMessages(response.data);
      setLoading(false);
    } catch (err) {
      console.error('Error fetching messages:', err);
      setError('Failed to load messages');
      setLoading(false);
    }
  };

  const markAsRead = async (messageId) => {
    try {
      const token = localStorage.getItem('token');
      await axios.patch(`http://localhost:5001/api/messages/${messageId}/read`, {}, {
        headers: { Authorization: `Bearer ${token}` }
      });

      // Update the message in the local state
      setMessages(messages.map(msg => 
        msg._id === messageId ? { ...msg, read: true } : msg
      ));
    } catch (err) {
      console.error('Error marking message as read:', err);
    }
  };

  const deleteMessage = async (messageId) => {
    try {
      const token = localStorage.getItem('token');
      await axios.delete(`http://localhost:5001/api/messages/${messageId}`, {
        headers: { Authorization: `Bearer ${token}` }
      });

      // Remove the message from the local state
      setMessages(messages.filter(msg => msg._id !== messageId));
    } catch (err) {
      console.error('Error deleting message:', err);
    }
  };

  const formatDate = (dateString) => {
    const options = { year: 'numeric', month: 'short', day: 'numeric', hour: '2-digit', minute: '2-digit' };
    return new Date(dateString).toLocaleDateString(undefined, options);
  };

  if (loading) return <div className="inbox-container">Loading...</div>;
  if (error) return <div className="inbox-container error">{error}</div>;

  return (
    <div className="inbox-container">
      <h1>Inbox</h1>
      {messages.length === 0 ? (
        <p className="no-messages">No messages</p>
      ) : (
        <div className="messages-list">
          {messages.map((message) => (
            <div 
              key={message._id} 
              className={`message-item ${!message.read ? 'unread' : ''}`}
              onClick={() => markAsRead(message._id)}
            >
              <div className="message-header">
                <img 
                  src={message.sender.picture || '/default-avatar.png'} 
                  alt={message.sender.name} 
                  className="sender-avatar"
                />
                <div className="message-info">
                  <h3>{message.sender.name}</h3>
                  <span className="message-date">{formatDate(message.createdAt)}</span>
                </div>
                {message.type === 'AUDIO_REQUEST' && (
                  <span className="message-type">Audio Request</span>
                )}
              </div>
              <p className="message-content">{message.content}</p>
              {message.type === 'AUDIO_REQUEST' && message.requestDetails && (
                <div className="request-details">
                  <p>Price: ${message.requestDetails.price}</p>
                  <p>Status: {message.requestDetails.status}</p>
                </div>
              )}
              <div className="message-actions">
                {message.type === 'AUDIO_REQUEST' && message.requestDetails?.status === 'PENDING' && (
                  <>
                    <button className="accept-btn">Accept</button>
                    <button className="decline-btn">Decline</button>
                  </>
                )}
                <button 
                  className="delete-btn"
                  onClick={(e) => {
                    e.stopPropagation();
                    deleteMessage(message._id);
                  }}
                >
                  Delete
                </button>
              </div>
            </div>
          ))}
        </div>
      )}
    </div>
  );
};

export default Inbox;
