import React, { useState, useEffect, useRef } from 'react';
import { FaPlus, FaTrash, FaEdit, FaGripLines, FaLink, FaMusic, FaVideo, FaPodcast, FaGlobe, FaTwitter, FaInstagram, FaFacebook, FaYoutube, FaSoundcloud, FaSpotify } from 'react-icons/fa';
import './Profile.css';

const Profile = () => {
  const [user, setUser] = useState(null);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState('');
  const [isEditing, setIsEditing] = useState(false);
  const [editedName, setEditedName] = useState('');
  const [editedBio, setEditedBio] = useState('');
  const [editedDisplayName, setEditedDisplayName] = useState('');
  const [editedLocation, setEditedLocation] = useState('');
  const [editedProfession, setEditedProfession] = useState('');
  const [editedProfileTheme, setEditedProfileTheme] = useState('default');
  const [selectedImage, setSelectedImage] = useState(null);
  const [imagePreview, setImagePreview] = useState(null);
  const fileInputRef = useRef(null);

  // Media links state
  const [mediaLinks, setMediaLinks] = useState([]);
  const [showAddLinkForm, setShowAddLinkForm] = useState(false);
  const [editingLinkId, setEditingLinkId] = useState(null);
  const [newLink, setNewLink] = useState({
    title: '',
    url: '',
    type: 'website',
    icon: 'link'
  });

  useEffect(() => {
    fetchUserProfile();
  }, []);

  const fetchUserProfile = async () => {
    try {
      const token = localStorage.getItem('token');
      const response = await fetch('http://localhost:5001/api/users/profile', {
        headers: {
          'Authorization': `Bearer ${token}`
        }
      });

      if (!response.ok) {
        throw new Error('Failed to fetch profile');
      }

      const userData = await response.json();
      setUser(userData);
      setEditedName(userData.name);
      setEditedBio(userData.bio || '');
      setEditedDisplayName(userData.displayName || '');
      setEditedLocation(userData.location || '');
      setEditedProfession(userData.profession || '');
      setEditedProfileTheme(userData.profileTheme || 'default');
      setImagePreview(userData.picture);
      setMediaLinks(userData.mediaLinks || []);
      setLoading(false);
    } catch (err) {
      setError(err.message);
      setLoading(false);
    }
  };

  const handleImageChange = (e) => {
    const file = e.target.files[0];
    if (file) {
      setSelectedImage(file);
      const reader = new FileReader();
      reader.onloadend = () => {
        setImagePreview(reader.result);
      };
      reader.readAsDataURL(file);
    }
  };

  const handleSaveProfile = async () => {
    try {
      const token = localStorage.getItem('token');
      const formData = new FormData();
      formData.append('name', editedName);
      formData.append('bio', editedBio);
      formData.append('displayName', editedDisplayName);
      formData.append('location', editedLocation);
      formData.append('profession', editedProfession);
      formData.append('profileTheme', editedProfileTheme);

      if (selectedImage) {
        formData.append('picture', selectedImage);
      }

      const response = await fetch('http://localhost:5001/api/users/profile', {
        method: 'PATCH',
        headers: {
          'Authorization': `Bearer ${token}`
        },
        body: formData
      });

      if (!response.ok) {
        throw new Error('Failed to update profile');
      }

      const updatedUser = await response.json();
      setUser(updatedUser);
      setIsEditing(false);
      setSelectedImage(null);
    } catch (err) {
      setError(err.message);
    }
  };

  // Media link functions
  const handleAddLink = async () => {
    try {
      if (!newLink.title || !newLink.url) {
        setError('Title and URL are required');
        return;
      }

      const token = localStorage.getItem('token');
      const response = await fetch('http://localhost:5001/api/users/profile/media-links', {
        method: 'POST',
        headers: {
          'Authorization': `Bearer ${token}`,
          'Content-Type': 'application/json'
        },
        body: JSON.stringify(newLink)
      });

      if (!response.ok) {
        throw new Error('Failed to add link');
      }

      const addedLink = await response.json();
      setMediaLinks([...mediaLinks, addedLink]);
      setNewLink({
        title: '',
        url: '',
        type: 'website',
        icon: 'link'
      });
      setShowAddLinkForm(false);
    } catch (err) {
      setError(err.message);
    }
  };

  const handleUpdateLink = async () => {
    try {
      if (!newLink.title || !newLink.url) {
        setError('Title and URL are required');
        return;
      }

      const token = localStorage.getItem('token');
      const response = await fetch(`http://localhost:5001/api/users/profile/media-links/${editingLinkId}`, {
        method: 'PATCH',
        headers: {
          'Authorization': `Bearer ${token}`,
          'Content-Type': 'application/json'
        },
        body: JSON.stringify(newLink)
      });

      if (!response.ok) {
        throw new Error('Failed to update link');
      }

      const updatedLink = await response.json();
      setMediaLinks(mediaLinks.map(link => 
        link._id === editingLinkId ? updatedLink : link
      ));
      setNewLink({
        title: '',
        url: '',
        type: 'website',
        icon: 'link'
      });
      setEditingLinkId(null);
    } catch (err) {
      setError(err.message);
    }
  };

  const handleDeleteLink = async (linkId) => {
    try {
      const token = localStorage.getItem('token');
      const response = await fetch(`http://localhost:5001/api/users/profile/media-links/${linkId}`, {
        method: 'DELETE',
        headers: {
          'Authorization': `Bearer ${token}`
        }
      });

      if (!response.ok) {
        throw new Error('Failed to delete link');
      }

      setMediaLinks(mediaLinks.filter(link => link._id !== linkId));
    } catch (err) {
      setError(err.message);
    }
  };

  const handleEditLink = (link) => {
    setNewLink({
      title: link.title,
      url: link.url,
      type: link.type,
      icon: link.icon
    });
    setEditingLinkId(link._id);
    setShowAddLinkForm(true);
  };

  const getIconComponent = (type, icon) => {
    switch (type) {
      case 'music':
        return <FaMusic />;
      case 'video':
        return <FaVideo />;
      case 'podcast':
        return <FaPodcast />;
      case 'social':
        switch (icon) {
          case 'twitter':
            return <FaTwitter />;
          case 'instagram':
            return <FaInstagram />;
          case 'facebook':
            return <FaFacebook />;
          case 'youtube':
            return <FaYoutube />;
          case 'soundcloud':
            return <FaSoundcloud />;
          case 'spotify':
            return <FaSpotify />;
          default:
            return <FaLink />;
        }
      case 'website':
      default:
        return <FaGlobe />;
    }
  };

  if (loading) {
    return <div className="profile-container loading">Loading...</div>;
  }

  if (error) {
    return <div className="profile-container error">{error}</div>;
  }

  return (
    <div className="profile-container">
      <div className="profile-header">
        <h1>My Profile</h1>
        {!isEditing && (
          <button className="edit-button" onClick={() => setIsEditing(true)}>
            Edit Profile
          </button>
        )}
      </div>

      <div className="profile-content">
        <div className="profile-image-section">
          <div className="profile-image">
            {imagePreview ? (
              <img src={imagePreview} alt="Profile" />
            ) : (
              <div className="profile-image-placeholder">
                {user?.name?.[0]?.toUpperCase()}
              </div>
            )}
            {isEditing && (
              <>
                <button
                  className="change-image-button"
                  onClick={() => fileInputRef.current.click()}
                >
                  Change Image
                </button>
                <input
                  ref={fileInputRef}
                  type="file"
                  accept="image/*"
                  onChange={handleImageChange}
                  style={{ display: 'none' }}
                />
              </>
            )}
          </div>
        </div>

        <div className="profile-details">
          {isEditing ? (
            <>
              <div className="form-group">
                <label>Name:</label>
                <input
                  type="text"
                  value={editedName}
                  onChange={(e) => setEditedName(e.target.value)}
                  placeholder="Enter your name"
                />
              </div>
              <div className="form-group">
                <label>Display Name (optional):</label>
                <input
                  type="text"
                  value={editedDisplayName}
                  onChange={(e) => setEditedDisplayName(e.target.value)}
                  placeholder="Enter a display name"
                />
              </div>
              <div className="form-group">
                <label>Location (optional):</label>
                <input
                  type="text"
                  value={editedLocation}
                  onChange={(e) => setEditedLocation(e.target.value)}
                  placeholder="Enter your location"
                />
              </div>
              <div className="form-group">
                <label>Profession (optional):</label>
                <input
                  type="text"
                  value={editedProfession}
                  onChange={(e) => setEditedProfession(e.target.value)}
                  placeholder="Enter your profession"
                />
              </div>
              <div className="form-group">
                <label>Bio:</label>
                <textarea
                  value={editedBio}
                  onChange={(e) => setEditedBio(e.target.value)}
                  placeholder="Tell us about yourself"
                  rows={4}
                />
              </div>
              <div className="form-group">
                <label>Profile Theme:</label>
                <select 
                  value={editedProfileTheme} 
                  onChange={(e) => setEditedProfileTheme(e.target.value)}
                >
                  <option value="default">Default</option>
                  <option value="dark">Dark</option>
                  <option value="light">Light</option>
                  <option value="colorful">Colorful</option>
                </select>
              </div>
              <div className="profile-actions">
                <button className="save-button" onClick={handleSaveProfile}>
                  Save Changes
                </button>
                <button
                  className="cancel-button"
                  onClick={() => {
                    setIsEditing(false);
                    setEditedName(user.name);
                    setEditedBio(user.bio || '');
                    setEditedDisplayName(user.displayName || '');
                    setEditedLocation(user.location || '');
                    setEditedProfession(user.profession || '');
                    setEditedProfileTheme(user.profileTheme || 'default');
                    setImagePreview(user.picture);
                    setSelectedImage(null);
                  }}
                >
                  Cancel
                </button>
              </div>
            </>
          ) : (
            <>
              <h2>{user.displayName || user.name}</h2>
              {user.profession && <p className="profession">{user.profession}</p>}
              {user.location && <p className="location">{user.location}</p>}
              <p className="bio">{user.bio || 'No bio available'}</p>
            </>
          )}
        </div>
      </div>

      <div className="media-links-section">
        <div className="section-header">
          <h2>My Links</h2>
          <button 
            className="add-link-button"
            onClick={() => {
              setShowAddLinkForm(true);
              setEditingLinkId(null);
              setNewLink({
                title: '',
                url: '',
                type: 'website',
                icon: 'link'
              });
            }}
          >
            <FaPlus /> Add Link
          </button>
        </div>

        {showAddLinkForm && (
          <div className="link-form">
            <h3>{editingLinkId ? 'Edit Link' : 'Add New Link'}</h3>
            <div className="form-group">
              <label>Title:</label>
              <input
                type="text"
                value={newLink.title}
                onChange={(e) => setNewLink({...newLink, title: e.target.value})}
                placeholder="Enter link title"
              />
            </div>
            <div className="form-group">
              <label>URL:</label>
              <input
                type="text"
                value={newLink.url}
                onChange={(e) => setNewLink({...newLink, url: e.target.value})}
                placeholder="Enter URL (https://...)"
              />
            </div>
            <div className="form-group">
              <label>Type:</label>
              <select
                value={newLink.type}
                onChange={(e) => setNewLink({...newLink, type: e.target.value})}
              >
                <option value="website">Website</option>
                <option value="social">Social Media</option>
                <option value="music">Music</option>
                <option value="video">Video</option>
                <option value="podcast">Podcast</option>
                <option value="other">Other</option>
              </select>
            </div>
            {newLink.type === 'social' && (
              <div className="form-group">
                <label>Platform:</label>
                <select
                  value={newLink.icon}
                  onChange={(e) => setNewLink({...newLink, icon: e.target.value})}
                >
                  <option value="twitter">Twitter</option>
                  <option value="instagram">Instagram</option>
                  <option value="facebook">Facebook</option>
                  <option value="youtube">YouTube</option>
                  <option value="soundcloud">SoundCloud</option>
                  <option value="spotify">Spotify</option>
                  <option value="link">Other</option>
                </select>
              </div>
            )}
            <div className="link-form-actions">
              <button 
                className="save-button"
                onClick={editingLinkId ? handleUpdateLink : handleAddLink}
              >
                {editingLinkId ? 'Update Link' : 'Add Link'}
              </button>
              <button 
                className="cancel-button"
                onClick={() => {
                  setShowAddLinkForm(false);
                  setEditingLinkId(null);
                }}
              >
                Cancel
              </button>
            </div>
          </div>
        )}

        <div className="links-preview">
          {mediaLinks.length === 0 ? (
            <p className="no-links">No links added yet. Add your first link to create your Linktree-style profile.</p>
          ) : (
            <div className="links-list">
              {mediaLinks.map((link) => (
                <div key={link._id} className="link-item">
                  <div className="link-drag-handle">
                    <FaGripLines />
                  </div>
                  <div className="link-icon">
                    {getIconComponent(link.type, link.icon)}
                  </div>
                  <div className="link-content">
                    <h3>{link.title}</h3>
                    <p className="link-url">{link.url}</p>
                  </div>
                  <div className="link-actions">
                    <button 
                      className="edit-link-button"
                      onClick={() => handleEditLink(link)}
                    >
                      <FaEdit />
                    </button>
                    <button 
                      className="delete-link-button"
                      onClick={() => handleDeleteLink(link._id)}
                    >
                      <FaTrash />
                    </button>
                  </div>
                </div>
              ))}
            </div>
          )}
        </div>
      </div>
    </div>
  );
};

export default Profile;
