import React, { useState, useEffect } from 'react';
import { useNavigate, Link } from 'react-router-dom';
import { GoogleLogin } from '@react-oauth/google';
import { jwtDecode } from 'jwt-decode';
import AudioRecorder from '../components/AudioRecorder';
import './Home.css';

const Home = () => {
  const [isRecording, setIsRecording] = useState(false);
  const [audioBlob, setAudioBlob] = useState(null);
  const [audioUrl, setAudioUrl] = useState(null);
  const [mediaRecorder, setMediaRecorder] = useState(null);
  const [recordingTitle, setRecordingTitle] = useState('');
  const [showSaveForm, setShowSaveForm] = useState(false);
  const [error, setError] = useState('');
  const [selectedArtwork, setSelectedArtwork] = useState(null);
  const [artworkPreview, setArtworkPreview] = useState(null);
  const [isAuthenticated, setIsAuthenticated] = useState(false);
  const navigate = useNavigate();

  useEffect(() => {
    const token = localStorage.getItem('token');
    setIsAuthenticated(!!token);
  }, []);

  const handleGoogleSuccess = async (credentialResponse) => {
    try {
      const decoded = jwtDecode(credentialResponse.credential);
      const response = await fetch('http://localhost:5001/api/auth/google', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({
          email: decoded.email,
          name: decoded.name,
          googleId: decoded.sub,
          picture: decoded.picture
        }),
      });

      const data = await response.json();

      if (!response.ok) {
        throw new Error(data.message || 'Google login failed');
      }

      localStorage.setItem('token', data.token);
      localStorage.setItem('userId', data.userId);
      setIsAuthenticated(true);
      window.location.reload(); // Reload to update all components with auth state
    } catch (err) {
      setError(err.message);
    }
  };

  const handleGoogleError = () => {
    setError('Google login failed. Please try again.');
  };

  const startRecording = async () => {
    try {
      const stream = await navigator.mediaDevices.getUserMedia({ audio: true });
      const recorder = new MediaRecorder(stream);
      const chunks = [];

      recorder.ondataavailable = (e) => chunks.push(e.data);
      recorder.onstop = () => {
        const blob = new Blob(chunks, { type: 'audio/wav' });
        setAudioBlob(blob);
        setAudioUrl(URL.createObjectURL(blob));
        setShowSaveForm(true);
      };

      recorder.start();
      setMediaRecorder(recorder);
      setIsRecording(true);
      setError('');
    } catch (err) {
      setError('Error accessing microphone: ' + err.message);
    }
  };

  const stopRecording = () => {
    if (mediaRecorder && mediaRecorder.state !== 'inactive') {
      mediaRecorder.stop();
      mediaRecorder.stream.getTracks().forEach(track => track.stop());
      setIsRecording(false);
    }
  };

  const handleArtworkChange = (e) => {
    const file = e.target.files[0];
    if (file) {
      setSelectedArtwork(file);
      const reader = new FileReader();
      reader.onloadend = () => {
        setArtworkPreview(reader.result);
      };
      reader.readAsDataURL(file);
    }
  };

  const handleSave = async () => {
    if (!audioBlob) {
      setError('No recording to save');
      return;
    }

    try {
      const formData = new FormData();
      formData.append('audio', audioBlob, 'recording.webm');
      formData.append('title', recordingTitle);
      if (selectedArtwork) {
        formData.append('artwork', selectedArtwork);
      }

      const token = localStorage.getItem('token');
      const response = await fetch('http://localhost:5001/api/recordings', {
        method: 'POST',
        headers: {
          'Authorization': `Bearer ${token}`
        },
        body: formData
      });

      if (!response.ok) {
        throw new Error('Failed to save recording');
      }

      setRecordingTitle('');
      setSelectedArtwork(null);
      setArtworkPreview(null);
      setShowSaveForm(false);
      setAudioBlob(null);
      setAudioUrl(null);
      navigate('/recordings');
    } catch (err) {
      setError(err.message);
    }
  };

  const handleDiscard = () => {
    setAudioBlob(null);
    setAudioUrl(null);
    setRecordingTitle('');
    setShowSaveForm(false);
    setSelectedArtwork(null);
    setArtworkPreview(null);
  };

  if (!isAuthenticated) {
    return (
      <div className="home-container">
        <div className="welcome-section">
          <h1>Welcome to VoiceGram</h1>
          <p>Record, manage, and share your audio messages with ease.</p>

          <div className="auth-section">
            <div className="google-login-container">
              <GoogleLogin
                onSuccess={handleGoogleSuccess}
                onError={handleGoogleError}
                useOneTap
              />
            </div>

            <div className="divider">
              <span>or</span>
            </div>

            <Link to="/login" className="login-link">
              Sign in with Email
            </Link>
          </div>

          {error && <div className="error-message">{error}</div>}
        </div>
      </div>
    );
  }

  return (
    <div className="home-container">
      <h1>Record Your Message</h1>
      <div className="recording-section">
        {error && <div className="error-message">{error}</div>}

        <div className="recording-controls">
          {!showSaveForm ? (
            <button 
              className={isRecording ? 'stop-button' : 'record-button'}
              onClick={isRecording ? stopRecording : startRecording}
            >
              {isRecording ? 'Stop Recording' : 'Start Recording'}
            </button>
          ) : (
            <div className="save-form">
              <h2>Save Recording</h2>
              <div className="form-group">
                <label>Title:</label>
                <input
                  type="text"
                  value={recordingTitle}
                  onChange={(e) => setRecordingTitle(e.target.value)}
                  placeholder="Enter a title"
                />
              </div>
              <div className="form-group">
                <label>Artwork:</label>
                <input
                  type="file"
                  accept="image/*"
                  onChange={handleArtworkChange}
                  className="file-input"
                />
                {artworkPreview && (
                  <div className="artwork-preview">
                    <img src={artworkPreview} alt="Recording artwork preview" />
                  </div>
                )}
              </div>
              <div className="save-actions">
                <button type="button" className="save-button" onClick={handleSave}>Save Recording</button>
                <button type="button" className="discard-button" onClick={handleDiscard}>
                  Discard
                </button>
              </div>
            </div>
          )}
        </div>
      </div>

      <div className="featured-podcasters">
        <h2>Featured Podcasters</h2>
        <div className="podcaster-grid">
          {/* Placeholder for podcaster list */}
          <div className="empty-state">
            No podcasters available yet. Check back soon!
          </div>
        </div>
      </div>
    </div>
  );
};

export default Home;
