import React, { useState, useEffect } from 'react';
import { Link, useNavigate } from 'react-router-dom';
import axios from 'axios';
import SearchBar from './SearchBar';
import './Navbar.css';

const Navbar = () => {
  const navigate = useNavigate();
  const token = localStorage.getItem('token');
  const [unreadCount, setUnreadCount] = useState(0);

  useEffect(() => {
    if (token) {
      fetchUnreadCount();
    }
  }, [token]);

  const fetchUnreadCount = async () => {
    try {
      const response = await axios.get('http://localhost:5001/api/messages/unread', {
        headers: { Authorization: `Bearer ${token}` }
      });
      setUnreadCount(response.data.count);
    } catch (err) {
      console.error('Error fetching unread count:', err);
    }
  };

  const handleLogout = () => {
    localStorage.removeItem('token');
    localStorage.removeItem('userId');
    navigate('/login');
  };

  return (
    <nav className="navbar">
      <div className="navbar-left">
        <Link to="/" className="navbar-brand">AudioZoom</Link>
        {token && <SearchBar />}
      </div>
      <div className="navbar-links">
        {!token && (
          <Link to="/login">Login</Link>
        )}
        {token && (
          <>
            <Link to="/recordings">My Recordings</Link>
            <Link to="/upload">Upload Audio</Link>
            <Link to="/profile">Profile</Link>
            <Link to="/inbox" className="inbox-link">
              Inbox
              {unreadCount > 0 && <span className="unread-badge">{unreadCount}</span>}
            </Link>
            <button onClick={handleLogout} className="logout-button">Logout</button>
          </>
        )}
      </div>
    </nav>
  );
};

export default Navbar;
