import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import styled from 'styled-components';
import Button from '../components/Button';
import Card from '../components/Card';
import api from '../utils/api';
import './Inbox.css';

const EmptyState = styled.div`
  text-align: center;
  padding: 3rem 1rem;
  color: ${props => props.theme.colors.textLight};
`;

const ActionButton = styled(Button)`
  margin-left: 0.5rem;
`;

const Inbox = () => {
  const [messages, setMessages] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const navigate = useNavigate();

  useEffect(() => {
    fetchMessages();
  }, []);

  const fetchMessages = async () => {
    try {
      setLoading(true);
      const response = await api.get('/api/messages');
      setMessages(response.data);
      setError(null);
    } catch (err) {
      console.error('Error fetching messages:', err);
      if (err.response) {
        setError(err.response.data.message || 'Failed to load messages');
      } else if (err.request) {
        setError('Network error - could not connect to server');
      } else {
        setError(err.message || 'Failed to load messages');
      }
    } finally {
      setLoading(false);
    }
  };

  const markAsRead = async (messageId) => {
    try {
      await api.patch(`/api/messages/${messageId}/read`, {});

      // Update the message in the local state
      setMessages(messages.map(msg => 
        msg._id === messageId ? { ...msg, read: true } : msg
      ));
    } catch (err) {
      console.error('Error marking message as read:', err);
      // Show error toast or notification here if needed
    }
  };

  const deleteMessage = async (messageId) => {
    try {
      await api.delete(`/api/messages/${messageId}`);

      // Remove the message from the local state
      setMessages(messages.filter(msg => msg._id !== messageId));
    } catch (err) {
      console.error('Error deleting message:', err);
      // Show error toast or notification here if needed
    }
  };

  const handleAcceptRequest = async (messageId) => {
    try {
      // Implement request acceptance logic here
      console.log('Accepting request:', messageId);

      // Update the message status in the local state
      setMessages(messages.map(msg => 
        msg._id === messageId ? { 
          ...msg, 
          requestDetails: { 
            ...msg.requestDetails, 
            status: 'ACCEPTED' 
          } 
        } : msg
      ));
    } catch (err) {
      console.error('Error accepting request:', err);
    }
  };

  const handleDeclineRequest = async (messageId) => {
    try {
      // Implement request decline logic here
      console.log('Declining request:', messageId);

      // Update the message status in the local state
      setMessages(messages.map(msg => 
        msg._id === messageId ? { 
          ...msg, 
          requestDetails: { 
            ...msg.requestDetails, 
            status: 'DECLINED' 
          } 
        } : msg
      ));
    } catch (err) {
      console.error('Error declining request:', err);
    }
  };

  const formatDate = (dateString) => {
    const options = { year: 'numeric', month: 'short', day: 'numeric', hour: '2-digit', minute: '2-digit' };
    return new Date(dateString).toLocaleDateString(undefined, options);
  };

  const navigateToCompose = () => {
    navigate('/compose');
  };

  if (loading) {
    return (
      <div className="inbox-container">
        <h1>Inbox</h1>
        <div className="loading">Loading messages...</div>
      </div>
    );
  }

  return (
    <div className="inbox-container">
      <div className="inbox-header">
        <h1>Inbox</h1>
        <Button $variant="primary" onClick={navigateToCompose}>
          New Message
        </Button>
      </div>

      {error && <div className="error-message">{error}</div>}

      {!error && messages.length === 0 ? (
        <Card>
          <Card.Body>
            <EmptyState>
              <p>Your inbox is empty</p>
              <Button $variant="primary" onClick={navigateToCompose}>
                Send a Message
              </Button>
            </EmptyState>
          </Card.Body>
        </Card>
      ) : (
        <div className="messages-list">
          {messages.map((message) => (
            <div 
              key={message._id} 
              className={`message-item ${!message.read ? 'unread' : ''}`}
              onClick={() => markAsRead(message._id)}
            >
              <div className="message-header">
                <img 
                  src={message.sender.picture || '/default-avatar.png'} 
                  alt={message.sender.name} 
                  className="sender-avatar"
                />
                <div className="message-info">
                  <h3>{message.sender.name}</h3>
                  <span className="message-date">{formatDate(message.createdAt)}</span>
                </div>
                {message.type === 'AUDIO_REQUEST' && (
                  <span className="message-type">Audio Request</span>
                )}
              </div>
              <p className="message-content">{message.content}</p>
              {message.type === 'AUDIO_REQUEST' && message.requestDetails && (
                <div className="request-details">
                  <p>Price: ${message.requestDetails.price}</p>
                  <p>Status: {message.requestDetails.status}</p>
                </div>
              )}
              <div className="message-actions">
                {message.type === 'AUDIO_REQUEST' && message.requestDetails?.status === 'PENDING' && (
                  <>
                    <ActionButton 
                      $variant="primary" 
                      $size="small"
                      onClick={(e) => {
                        e.stopPropagation();
                        handleAcceptRequest(message._id);
                      }}
                    >
                      Accept
                    </ActionButton>
                    <ActionButton 
                      $variant="outline" 
                      $size="small"
                      onClick={(e) => {
                        e.stopPropagation();
                        handleDeclineRequest(message._id);
                      }}
                    >
                      Decline
                    </ActionButton>
                  </>
                )}
                <ActionButton 
                  $variant="danger" 
                  $size="small"
                  onClick={(e) => {
                    e.stopPropagation();
                    deleteMessage(message._id);
                  }}
                >
                  Delete
                </ActionButton>
              </div>
            </div>
          ))}
        </div>
      )}
    </div>
  );
};

export default Inbox;
