import React, { useState, useEffect, useRef } from 'react';
import { Link, useNavigate } from 'react-router-dom';
import { Badge } from 'react-bootstrap';
import { FaMicrophone, FaUpload } from 'react-icons/fa';
import api from '../utils/api';
import SearchUsers from './SearchUsers';
import Logo from './Logo';
import styled from 'styled-components';
import './Navbar.css';

const NavbarContainer = styled.nav`
  background: white;
  padding: 1rem 2rem;
  box-shadow: ${props => props.theme.shadows.small};
  display: flex;
  justify-content: space-between;
  align-items: center;
  position: sticky;
  top: 0;
  z-index: 1000;
`;

const NavLeft = styled.div`
  display: flex;
  align-items: center;
  gap: 2rem;
`;

const NavRight = styled.div`
  display: flex;
  align-items: center;
  gap: 1rem;
`;

const LoginButton = styled(Link)`
  background: ${props => props.theme.colors.primary};
  color: white;
  padding: 0.5rem 1.5rem;
  border-radius: ${props => props.theme.borderRadius.medium};
  text-decoration: none;
  font-weight: 500;
  transition: all 0.2s ease;

  &:hover {
    background: ${props => props.theme.colors.secondary};
    transform: translateY(-2px);
  }
`;

const RegisterButton = styled(Link)`
  background: transparent;
  color: ${props => props.theme.colors.primary};
  padding: 0.5rem 1.5rem;
  border-radius: ${props => props.theme.borderRadius.medium};
  text-decoration: none;
  font-weight: 500;
  border: 2px solid ${props => props.theme.colors.primary};
  transition: all 0.2s ease;

  &:hover {
    background: ${props => props.theme.colors.primary};
    color: white;
    transform: translateY(-2px);
  }
`;

const Navbar = () => {
  const navigate = useNavigate();
  const token = localStorage.getItem('token');
  const [unreadRequests, setUnreadRequests] = useState(0);
  const [unreadMessages, setUnreadMessages] = useState(0);
  const [showAudioOptions, setShowAudioOptions] = useState(false);
  const dropdownRef = useRef(null);

  useEffect(() => {
    if (token) {
      fetchUnreadRequests();
      fetchUnreadMessages();

      // Set up polling to check for new messages every minute
      const intervalId = setInterval(() => {
        fetchUnreadMessages();
        fetchUnreadRequests();
      }, 60000); // 60 seconds

      return () => clearInterval(intervalId);
    }
  }, [token]);

  useEffect(() => {
    const handleClickOutside = (event) => {
      if (dropdownRef.current && !dropdownRef.current.contains(event.target)) {
        setShowAudioOptions(false);
      }
    };

    document.addEventListener('mousedown', handleClickOutside);
    return () => document.removeEventListener('mousedown', handleClickOutside);
  }, []);

  const fetchUnreadRequests = async () => {
    try {
      const response = await api.get('/api/audio-requests/my-orders');

      // Get viewed requests from localStorage
      const storedViewedRequests = localStorage.getItem('viewedRequests');
      const viewedRequests = storedViewedRequests ? new Set(JSON.parse(storedViewedRequests)) : new Set();

      // Count unread pending requests
      const unreadCount = response.data.filter(
        req => !viewedRequests.has(req._id) && req.status === 'pending'
      ).length;

      setUnreadRequests(unreadCount);
    } catch (err) {
      console.error('Error fetching unread requests:', err);
    }
  };

  const fetchUnreadMessages = async () => {
    try {
      const response = await api.get('/api/messages/unread');
      setUnreadMessages(response.data.count);
    } catch (err) {
      console.error('Error fetching unread messages:', err);
    }
  };

  const handleLogout = () => {
    localStorage.removeItem('token');
    localStorage.removeItem('userId');
    navigate('/login');
  };

  const toggleAudioOptions = () => {
    setShowAudioOptions(!showAudioOptions);
  };

  return (
    <NavbarContainer>
      <NavLeft>
        <Link to="/">
          <Logo size="medium" />
        </Link>
      </NavLeft>
      <NavRight>
        <LoginButton to="/login">Login</LoginButton>
        <RegisterButton to="/register">Sign Up</RegisterButton>
      </NavRight>
    </NavbarContainer>
  );
};

export default Navbar;
