import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import './Recordings.css';

const Recordings = () => {
  const [recordings, setRecordings] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const [description, setDescription] = useState('');
  const [editingId, setEditingId] = useState(null);
  const [currentlyPlaying, setCurrentlyPlaying] = useState(null);
  const navigate = useNavigate();

  useEffect(() => {
    const token = localStorage.getItem('token');
    if (!token) {
      navigate('/login');
      return;
    }
    fetchRecordings();
  }, [navigate]);

  const fetchRecordings = async () => {
    try {
      const token = localStorage.getItem('token');
      const response = await fetch('http://localhost:5001/api/recordings', {
        headers: {
          'Authorization': `Bearer ${token}`
        }
      });
      if (!response.ok) {
        throw new Error('Failed to fetch recordings');
      }
      const data = await response.json();
      setRecordings(data);
      setLoading(false);
    } catch (err) {
      setError(err.message);
      setLoading(false);
    }
  };

  const handleDelete = async (id) => {
    try {
      const token = localStorage.getItem('token');
      const response = await fetch(`http://localhost:5001/api/recordings/${id}`, {
        method: 'DELETE',
        headers: {
          'Authorization': `Bearer ${token}`
        }
      });
      if (!response.ok) {
        throw new Error('Failed to delete recording');
      }
      fetchRecordings();
    } catch (err) {
      setError(err.message);
    }
  };

  const handleUpdateDescription = async (id) => {
    try {
      const token = localStorage.getItem('token');
      const response = await fetch(`http://localhost:5001/api/recordings/${id}`, {
        method: 'PATCH',
        headers: {
          'Content-Type': 'application/json',
          'Authorization': `Bearer ${token}`
        },
        body: JSON.stringify({ description })
      });
      if (!response.ok) {
        throw new Error('Failed to update description');
      }
      fetchRecordings();
      setEditingId(null);
      setDescription('');
    } catch (err) {
      setError(err.message);
    }
  };

  const handlePlay = (recordingId) => {
    // Stop currently playing audio if any
    if (currentlyPlaying && currentlyPlaying !== recordingId) {
      const audioElement = document.querySelector(`audio[data-recording-id="${currentlyPlaying}"]`);
      if (audioElement) {
        audioElement.pause();
        audioElement.currentTime = 0;
      }
    }
    setCurrentlyPlaying(recordingId);
  };

  const handlePause = () => {
    setCurrentlyPlaying(null);
  };

  const formatDate = (dateString) => {
    return new Date(dateString).toLocaleString('en-US', {
      year: 'numeric',
      month: 'long',
      day: 'numeric',
      hour: '2-digit',
      minute: '2-digit'
    });
  };

  if (loading) {
    return <div className="recordings-container loading">Loading...</div>;
  }

  if (error) {
    return <div className="recordings-container error">{error}</div>;
  }

  return (
    <div className="recordings-container">
      <h1>My Recordings</h1>
      {recordings.length === 0 ? (
        <p className="no-recordings">No recordings found. Start recording to see them here!</p>
      ) : (
        <div className="recordings-list">
          {recordings.map((recording) => (
            <div key={recording._id} className="recording-item">
              <div className="recording-info">
                <h3>{recording.title || 'Untitled Recording'}</h3>
                <p className="recording-date">{formatDate(recording.createdAt)}</p>
              </div>
              <div className="recording-description">
                {editingId === recording._id ? (
                  <div className="description-edit">
                    <textarea
                      value={description || recording.description}
                      onChange={(e) => setDescription(e.target.value)}
                      placeholder="Add a description..."
                    />
                    <div className="description-actions">
                      <button onClick={() => handleUpdateDescription(recording._id)}>Save</button>
                      <button onClick={() => {
                        setEditingId(null);
                        setDescription('');
                      }}>Cancel</button>
                    </div>
                  </div>
                ) : (
                  <div className="description-display">
                    <p>{recording.description || 'No description'}</p>
                    <button onClick={() => {
                      setEditingId(recording._id);
                      setDescription(recording.description || '');
                    }}>Edit Description</button>
                  </div>
                )}
              </div>
              <div className="recording-player">
                <div className="audio-controls">
                  <button
                    className={`play-button ${currentlyPlaying === recording._id ? 'playing' : ''}`}
                    onClick={() => currentlyPlaying === recording._id ? handlePause() : handlePlay(recording._id)}
                  >
                    {currentlyPlaying === recording._id ? 'Pause' : 'Play'}
                  </button>
                  <audio
                    data-recording-id={recording._id}
                    src={`http://localhost:5001${recording.url}`}
                    onEnded={() => setCurrentlyPlaying(null)}
                    onPlay={() => handlePlay(recording._id)}
                    onPause={() => handlePause()}
                    controls
                  />
                </div>
                <button 
                  className="delete-button"
                  onClick={() => handleDelete(recording._id)}
                >
                  Delete
                </button>
              </div>
            </div>
          ))}
        </div>
      )}
    </div>
  );
};

export default Recordings;
