import React, { useState, useEffect } from 'react';
import PodcasterCard from '../components/PodcasterCard';
import './Home.css';

const Home = () => {
  const [podcasters, setPodcasters] = useState([]);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    fetchPodcasters();
  }, []);

  const fetchPodcasters = async () => {
    try {
      const response = await fetch('/api/podcasters');
      const data = await response.json();
      setPodcasters(data);
      setLoading(false);
    } catch (error) {
      console.error('Error fetching podcasters:', error);
      setLoading(false);
    }
  };

  const handleRequestClick = (podcaster) => {
    // Navigate to request form or open modal
  };

  if (loading) {
    return <div>Loading...</div>;
  }

  return (
    <div className="home-container">
      <h1>VoiceGram</h1>
      <p>Request personalized audio messages from your favorite podcasters</p>

      <div className="podcasters-grid">
        {podcasters.map(podcaster => (
          <PodcasterCard
            key={podcaster._id}
            podcaster={podcaster}
            onRequestClick={handleRequestClick}
          />
        ))}
      </div>
    </div>
  );
};

export default Home;
