import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import AudioRecorder from '../components/AudioRecorder';
import './Home.css';

const Home = () => {
  const [isRecording, setIsRecording] = useState(false);
  const [audioBlob, setAudioBlob] = useState(null);
  const [audioUrl, setAudioUrl] = useState(null);
  const [mediaRecorder, setMediaRecorder] = useState(null);
  const [recordingTitle, setRecordingTitle] = useState('');
  const [showSaveForm, setShowSaveForm] = useState(false);
  const [error, setError] = useState('');
  const navigate = useNavigate();

  const startRecording = async () => {
    try {
      const stream = await navigator.mediaDevices.getUserMedia({ audio: true });
      const recorder = new MediaRecorder(stream);
      const chunks = [];

      recorder.ondataavailable = (e) => chunks.push(e.data);
      recorder.onstop = () => {
        const blob = new Blob(chunks, { type: 'audio/wav' });
        setAudioBlob(blob);
        setAudioUrl(URL.createObjectURL(blob));
        setShowSaveForm(true);
      };

      recorder.start();
      setMediaRecorder(recorder);
      setIsRecording(true);
      setError('');
    } catch (err) {
      setError('Error accessing microphone: ' + err.message);
    }
  };

  const stopRecording = () => {
    if (mediaRecorder && mediaRecorder.state !== 'inactive') {
      mediaRecorder.stop();
      mediaRecorder.stream.getTracks().forEach(track => track.stop());
      setIsRecording(false);
    }
  };

  const handleSave = async (e) => {
    e.preventDefault();

    if (!audioBlob) {
      setError('No recording to save');
      return;
    }

    const token = localStorage.getItem('token');
    if (!token) {
      navigate('/login');
      return;
    }

    try {
      const formData = new FormData();
      formData.append('audio', audioBlob, 'recording.wav');
      formData.append('title', recordingTitle || 'Untitled Recording');

      const response = await fetch('http://localhost:5001/api/recordings', {
        method: 'POST',
        headers: {
          'Authorization': `Bearer ${token}`
        },
        body: formData
      });

      if (!response.ok) {
        throw new Error('Failed to save recording');
      }

      // Reset the form and state
      setAudioBlob(null);
      setAudioUrl(null);
      setRecordingTitle('');
      setShowSaveForm(false);

      // Navigate to recordings page
      navigate('/recordings');
    } catch (err) {
      setError(err.message);
    }
  };

  const handleDiscard = () => {
    setAudioBlob(null);
    setAudioUrl(null);
    setRecordingTitle('');
    setShowSaveForm(false);
  };

  return (
    <div className="home-container">
      <h1>Record Your Message</h1>
      <div className="recording-section">
        {error && <div className="error-message">{error}</div>}

        <div className="recording-controls">
          {!showSaveForm ? (
            <button 
              className={isRecording ? 'stop-button' : 'record-button'}
              onClick={isRecording ? stopRecording : startRecording}
            >
              {isRecording ? 'Stop Recording' : 'Start Recording'}
            </button>
          ) : (
            <div className="save-form">
              <h3>Save Your Recording</h3>
              <form onSubmit={handleSave}>
                <div className="form-group">
                  <label htmlFor="title">Title (optional)</label>
                  <input
                    type="text"
                    id="title"
                    value={recordingTitle}
                    onChange={(e) => setRecordingTitle(e.target.value)}
                    placeholder="Enter a title for your recording"
                  />
                </div>

                <div className="audio-preview">
                  <audio controls src={audioUrl} />
                </div>

                <div className="save-actions">
                  <button type="submit" className="save-button">Save Recording</button>
                  <button type="button" className="discard-button" onClick={handleDiscard}>
                    Discard
                  </button>
                </div>
              </form>
            </div>
          )}
        </div>
      </div>

      <div className="featured-podcasters">
        <h2>Featured Podcasters</h2>
        <div className="podcaster-grid">
          {/* Placeholder for podcaster list */}
          <div className="empty-state">
            No podcasters available yet. Check back soon!
          </div>
        </div>
      </div>
    </div>
  );
};

export default Home;
