import React from 'react';
import { BrowserRouter as Router, Routes, Route, Navigate } from 'react-router-dom';
import { GoogleOAuthProvider } from '@react-oauth/google';
import Navbar from './components/Navbar';
import Home from './pages/Home';
import Login from './pages/Login';
import Register from './pages/Register';
import Upload from './pages/Upload';
import UploadResult from './pages/UploadResult';
import AudioHistory from './pages/AudioHistory';
import Profile from './pages/Profile';
import PublicProfile from './pages/PublicProfile';
import AdminDashboard from './pages/AdminDashboard';
import RequestForm from './components/RequestForm';
import RequestConfirmation from './pages/RequestConfirmation';
import RequestInbox from './pages/RequestInbox';
import './App.css';

const GOOGLE_CLIENT_ID = process.env.REACT_APP_GOOGLE_CLIENT_ID || ''; // You'll need to add your client ID to .env file

const App = () => {
  return (
    <GoogleOAuthProvider clientId={GOOGLE_CLIENT_ID}>
      <Router>
        <div className="app">
          <Navbar />
          <main className="main-content">
            <Routes>
              <Route path="/login" element={<Login />} />
              <Route path="/register" element={<Register />} />
              <Route path="/upload" element={<Upload />} />
              <Route path="/upload/result" element={<UploadResult />} />
              <Route path="/audio-history" element={<AudioHistory />} />
              <Route path="/profile" element={<Profile />} />
              <Route path="/profile/:userId" element={<PublicProfile />} />
              <Route path="/user/:userId" element={<PublicProfile />} />
              <Route path="/request/:userId/:optionId" element={<RequestForm />} />
              <Route path="/request/confirmation/:requestId" element={<RequestConfirmation />} />
              <Route path="/requests" element={<RequestInbox />} />
              <Route path="/" element={<Home />} />
              <Route path="/admin/dashboard" element={<AdminDashboard />} />
              <Route path="*" element={<Navigate to="/" />} />
            </Routes>
          </main>
        </div>
      </Router>
    </GoogleOAuthProvider>
  );
};

export default App;
