import React, { useState, useEffect } from 'react';
import './Recordings.css';

const Recordings = () => {
  const [recordings, setRecordings] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  const fetchRecordings = async () => {
    try {
      const response = await fetch('/api/recordings');
      if (!response.ok) {
        throw new Error('Failed to fetch recordings');
      }
      const data = await response.json();
      setRecordings(data);
      setLoading(false);
    } catch (err) {
      setError(err.message);
      setLoading(false);
    }
  };

  useEffect(() => {
    fetchRecordings();
  }, []);

  const handleDelete = async (id) => {
    try {
      const response = await fetch(`/api/recordings/${id}`, {
        method: 'DELETE',
      });

      if (!response.ok) {
        throw new Error('Failed to delete recording');
      }

      // Refresh the recordings list
      fetchRecordings();
    } catch (err) {
      setError(err.message);
    }
  };

  if (loading) {
    return <div className="recordings-loading">Loading recordings...</div>;
  }

  if (error) {
    return <div className="recordings-error">Error: {error}</div>;
  }

  return (
    <div className="recordings-container">
      <h1>My Recordings</h1>
      {recordings.length === 0 ? (
        <div className="no-recordings">
          <p>You haven't made any recordings yet.</p>
        </div>
      ) : (
        <div className="recordings-list">
          {recordings.map((recording) => (
            <div key={recording.id} className="recording-item">
              <div className="recording-info">
                <h3>{recording.title || 'Untitled Recording'}</h3>
                <p className="recording-date">
                  {new Date(recording.createdAt).toLocaleDateString()}
                </p>
              </div>
              <audio src={recording.url} controls className="audio-player" />
              <button 
                className="delete-button"
                onClick={() => handleDelete(recording.id)}
              >
                Delete
              </button>
            </div>
          ))}
        </div>
      )}
    </div>
  );
};

export default Recordings;
